import React from "react";
import {
  Box,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Typography,
  useTheme,
  Divider
} from "@mui/material";
import { useSelector } from "react-redux";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import SingleList from "../../ShareModule/SingleList";
const ChatBox = () => {
    const theme = useTheme();
    const AccountList = useSelector(
        state => state.reducer?.MainData?.data
      );
    const handleClickAccount = () => {

    }
  return (
    <Box>
      <Accordion
        sx={{
          width: "15vw",
          "&.Mui-expanded .MuiAccordionSummary-root": {
            minHeight: theme.spacing(2), // Decrease the size of the summary when expanded
          },
        }}
      >
        <AccordionSummary
          expandIcon={<ExpandMoreIcon sx={{ color: "#fff" }} />}
          aria-controls="panel1a-content"
          id="panel1a-header"
          sx={{
            backgroundColor: "#00559A",
            minHeight: "10px",
            height: "35px",
            color: "#fff",
            borderRadius: "5px",
            mb: 0.5,
          }}
        >
          <Typography sx={{ color: "#fff" }}>Chat Box</Typography>
        </AccordionSummary>
        <AccordionDetails>
         <Box sx={{height:'10vh'}}>
         {AccountList.map((ele, index) => (
              <Box key={ele.id}>
                <div onClick={() => handleClickAccount(ele.id)}>
                  <SingleList
                    id={ele.id}
                    name={ele.username}
                    image={ele.profilepicture}
                  />
                </div>
                <Divider sx={{ mt: 1.5 }} light />
              </Box>
            ))}
         </Box>
        </AccordionDetails>
      </Accordion>
    </Box>
  );
};
export default ChatBox;
