import React,{useEffect,useState} from 'react'
import { useDispatch,useSelector } from 'react-redux'
import { addChatData } from '../../Reducer/reducer'
import { Box } from '@mui/material'
import { styled } from '@mui/material/styles';
import Badge from '@mui/material/Badge';
import Avatar from '@mui/material/Avatar';
import Stack from '@mui/material/Stack';
import './chat.css';

const StyledBadge = styled(Badge)(({ theme }) => ({
    '& .MuiBadge-badge': {
      backgroundColor: '#44b700',
      color: '#44b700',
      boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
      '&::after': {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        borderRadius: '50%',
        animation: 'ripple 1.2s infinite ease-in-out',
        border: '1px solid currentColor',
        content: '""',
      },
    },
    '@keyframes ripple': {
      '0%': {
        transform: 'scale(.8)',
        opacity: 1,
      },
      '100%': {
        transform: 'scale(2.4)',
        opacity: 0,
      },
    },
  }));

let AllChatData = [
    { id: 1, text: 'Hello!', sender: 'user' },
    { id: 2, text: 'Hi there!', sender: 'bot' },
    { id: 3, text: 'Hello!', sender: 'user' },
    { id: 4, text: 'Hi there!', sender: 'bot' },
    { id: 5, text: 'Hello!', sender: 'user' },
    { id: 6, text: 'Hi there!', sender: 'bot' },
    { id: 7, text: 'Hello!', sender: 'user' },
    { id: 8, text: 'Hi there!', sender: 'bot' },
]

const ChatList = () => {
    const dispatch = useDispatch()
    const ChatData  = useSelector(state=>state?.reducer?.ChatData?.chat)
    const userProfile = useSelector((state) => state.reducer?.ChatData?.chatProfile)
    useEffect(()=>{
        dispatch(addChatData(AllChatData))
    },[])
    return(
        <Box>
          <div className="chat-container">
          <div className='chat-profile'>
          <StyledBadge
        overlap="circular"
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
        variant="dot"
      >
        <Avatar alt="R" src="/static/images/avatar/1.jpg" sx={{width:'20px',height:'20px'}} />
      </StyledBadge>

          </div>
      <div className="chat-messages">
        {ChatData.map((message) => (
            <>
          <div
            key={message.id}
            className={`chat-message ${message.sender === 'user' ? 'user' : 'bot'}`}
          >
            {message.text}
          </div>
          <br/>
            </>
        ))}
      </div>
      <div className="chat-input">
        <input type="text" placeholder="Type your message" />
        <button className="send-button">Send</button>
      </div>
    </div>
        </Box>
    )
}
export default ChatList;
