import React,{useEffect,useState} from 'react'
import { useDispatch,useSelector } from 'react-redux'
import { addChatData } from '../../Reducer/reducer'
import { Box, Typography } from '@mui/material'
import Badge from '@mui/material/Badge';
import Avatar from '@mui/material/Avatar';
import Stack from '@mui/material/Stack';
import './chat.css';

let AllChatData = [
    { id: 1, text: 'Hello!', sender: 'user' },
    { id: 2, text: 'Hi there!', sender: 'bot' },
    { id: 3, text: 'Hello!', sender: 'user' },
    { id: 4, text: 'Hi there!', sender: 'bot' },
    { id: 5, text: 'Hello!', sender: 'user' },
    { id: 6, text: 'Hi there!', sender: 'bot' },
    { id: 7, text: 'Hello!', sender: 'user' },
    { id: 8, text: 'Hi there!', sender: 'bot' },
]

const ChatList = () => {
    const dispatch = useDispatch()
    const ChatData  = useSelector(state=>state?.reducer?.ChatData?.chat)
    const userProfile = useSelector((state) => state.reducer?.ChatData?.chatProfile)
    useEffect(()=>{
        dispatch(addChatData(AllChatData))
    },[])
    return(
        <Box>
          <div className="chat-container">
          <div className='chat-profile'>

        <Avatar alt="R" src="/static/images/avatar/1.jpg" sx={{width:'25px',height:'25px',mt:.5,}} />

       <Typography sx={{mt:1}}>Suresh Pradhan</Typography>

          </div>
      <div className="chat-messages">
        {ChatData.map((message) => (
            <>
          <div
            key={message.id}
            className={`chat-message ${message.sender === 'user' ? 'user' : 'bot'}`}
          >
            {message.text}
          </div>
          <br/>
            </>
        ))}
      </div>
      <div className="chat-input">
        <input type="text" placeholder="Type your message" />
        <button className="send-button">Send</button>
      </div>
    </div>
        </Box>
    )
}
export default ChatList;
